def sort_asc_desc_ending(input_str):
    input_list = input_str.strip().split('\n')
    n, m = map(int, input_list[0].split())
    books = [(input_list[i], i+1) for i in range(1, n+1)]

    def compare(book):
        return ''.join(sorted(book[0][::2])) + ''.join(sorted(book[0][1::2], reverse=True))

    sorted_books = sorted(books, key=compare)

    return ' '.join(str(book[1]) for book in sorted_books)
