def sort_books(n, m, books):
    def compare(a, b):
        for i in range(m):
            if i % 2 == 0:
                if a[i] > b[i]:
                    return -1
                elif a[i] < b[i]:
                    return 1
            else:
                if a[i] < b[i]:
                    return -1
                elif a[i] > b[i]:
                    return 1
        return 0

    book_indexes = list(range(1, n+1))
    book_indexes.sort(key=lambda x: books[x-1], reverse=True)
    book_indexes.sort(cmp=compare)
    return ' '.join(map(str, book_indexes))
