def count_changes(n, m, a, b):
    def calc_occurrences(a, b):
        occurrences = 0
        for i in range(n - m + 1):
            if a[i:i + m] == b:
                occurrences += 1
        return occurrences

    def diff(a, b):
        return sum(1 for x, y in zip(a, b) if x != y)

    counts = []
    for k in range(n - m + 2):
        max_occurrences = k
        min_changes = float('inf')
        for i in range(n - m + 1):
            if calc_occurrences(a, b) >= max_occurrences:
                min_changes = min(min_changes, diff(a, b * i + a[len(b * i):]))
        if min_changes == float('inf'):
            counts.append(-1)
        else:
            counts.append(min_changes)
    return counts
