def count_permutations(n, m, k, p):
    dp = [[0] * (k+1) for _ in range(n+1)]
    dp[0][0] = 1

    for i in range(1, n+1):
        for j in range(k+1):
            for x in range(1, min(i, m)+1):
                if j >= x:
                    dp[i][j] += dp[i-x][j-x]
                    dp[i][j] %= p

    return dp[n][k]

n = 5
m = 2
k = 1
p = 10**9 + 7
count_permutations(n, m, k, p)
