def find_minimal_difference(a, b, c):
    durations = [a, b*2, c*3]
    total_duration = sum(durations)

    half_duration = total_duration // 2
    dp = [False] * (half_duration + 1)
    dp[0] = True

    for duration in durations:
        for i in range(half_duration, duration - 1, -1):
            dp[i] = dp[i] or dp[i - duration]

    for i in range(half_duration, -1, -1):
        if dp[i]:
            return total_duration - 2 * i
