def minimal_difference(a, b, c):
    total_duration = a + 2 * b + 3 * c
    half_duration = total_duration // 2

    dp = [False] * (half_duration + 1)
    dp[0] = True

    for i in range(a + 1):
        for j in range(half_duration, i - 1, -1):
            dp[j] |= dp[j - i]

    for i in range(b + 1):
        for j in range(half_duration, 2*i - 1, -1):
            dp[j] |= dp[j - 2 * i]

    for i in range(c + 1):
        for j in range(half_duration, 3*i - 1, -1):
            dp[j] |= dp[j - 3 * i]

    for i in range(half_duration, -1, -1):
        if dp[i]:
            return total_duration - 2 * i
