def minimal_difference(a, b, c):
    durations = [1] * a + [2] * b + [3] * c
    total_duration = sum(durations)
    target_duration = total_duration // 2

    dp = [0] * (target_duration + 1)
    for duration in durations:
        for i in range(target_duration, duration-1, -1):
            dp[i] = max(dp[i], dp[i - duration] + duration)

    return total_duration - 2 * dp[target_duration]
