def minimal_difference(a, b, c):
    total_duration = a + 2 * b + 3 * c
    half_duration = total_duration // 2

    dp = [0] * (half_duration + 1)

    for i in range(a + 1):
        for j in range(b, -1, -1):
            for k in range(c, -1, -1):
                duration = i + 2 * j + 3 * k
                if duration <= half_duration:
                    dp[duration] = max(dp[duration], duration)

                dp[duration] = max(dp[duration], dp[duration - 1])

    return total_duration - 2 * dp[half_duration]
