def min_concert_duration_difference(a, b, c):
    total_duration = a + b + c
    half_duration = total_duration // 2

    min_difference = float('inf')

    for i in range(a+1):
        for j in range(b+1):
            k = half_duration - (i*1 + j*2)
            if 0 <= k <= c:
                difference = abs((i*1 + j*2 + k*3) - (total_duration - i*1 - j*2 - k*3))
                min_difference = min(min_difference, difference)

    return min_difference
