def min_concert_difference(a, b, c):
    total_duration = a + 2*b + 3*c
    target_duration = total_duration // 2

    # Dynamic Programming approach
    dp = [[float("inf") for _ in range(target_duration+1)] for _ in range(4)]

    dp[0][0] = dp[1][0] = dp[2][0] = dp[3][0] = 0

    for i in range(1, 4):
        for j in range(1, target_duration+1):
            if i == 1:
                if j >= 1:
                    dp[i][j] = min(dp[i][j], dp[i][j-1] + 1)
                    if j >= a:
                        dp[i][j] = min(dp[i][j], dp[i-1][j-a] + 1)
            elif i == 2:
                if j >= 2:
                    dp[i][j] = min(dp[i][j], dp[i][j-2] + 1)
                    if j >= 2*b:
                        dp[i][j] = min(dp[i][j], dp[i-1][j-2*b] + 1)
                    if j >= b and j % 2 == 0:
                        dp[i][j] = min(dp[i][j], dp[i-1][j-b] + 1)
            else:
                if j >= 3:
                    dp[i][j] = min(dp[i][j], dp[i][j-3] + 1)
                    if j >= 3*c:
                        dp[i][j] = min(dp[i][j], dp[i-1][j-3*c] + 1)
                    if j >= 2*c:
                        dp[i][j] = min(dp[i][j], dp[i-1][j-2*c] + 1)
                    if j >= c:
                        dp[i][j] = min(dp[i][j], dp[i-1][j-c] + 1)

    return (total_duration - 2 * dp[3][target_duration])
