def minimal_difference(a, b, c):
    total_time = a + b + c
    half_time = total_time // 2

    # Calculate all possible sums of song durations
    possible_sums = {0}
    for duration in [a, b, c]:
        for sum in list(possible_sums):
            possible_sums.add(sum + duration)

    # Find the closest sum to half_time
    min_difference = float('inf')
    for sum in possible_sums:
        difference = abs(half_time - sum)
        if difference < min_difference:
            min_difference = difference

    return min_difference
