def determine_winner(A):
    n = len(A)
    dp = [[0] * n for _ in range(n)]

    for i in range(n):
        dp[i][i] = 1

    for j in range(1, n):
        i = j - 1

        while i >= 0:
            if A[i] < A[i+1]:  # Alice's turn
                if dp[i+1][j] == 0 or dp[i][j-1] == 0:
                    dp[i][j] = 1
                else:
                    dp[i][j] = 0
            else:  # Bob's turn
                if dp[i+1][j] == 0 or dp[i][j-1] == 0:
                    dp[i][j] = 0
                else:
                    dp[i][j] = 1

            i -= 1

    return 'Alice' if dp[0][n-1] else 'Bob'
