def winner_of_game(array):
    # create a 2D dynamic programming table
    dp = [[0] * len(array) for _ in range(len(array))]

    # fill the dp table
    for i in range(len(array)):
        dp[i][i] = 1

    for length in range(2, len(array) + 1):
        for i in range(len(array) - length + 1):
            j = i + length - 1
            if array[i] < array[i + 1]:
                dp[i][j] = 1
            else:
                for k in range(i, j):
                    if dp[i][k] and dp[k + 1][j]:
                        dp[i][j] = 1
                        break

    # return the winner based on the value in the dp table
    if dp[0][-1] == 1:
        return 'Alice'
    else:
        return 'Bob'
