def winner_of_game(A):
    n = len(A)
    dp = [[0] * n for _ in range(n)]

    for i in range(n):
        dp[i][i] = A[i]

    for l in range(2, n+1):
        for i in range(n-l+1):
            j = i + l - 1

            if l == 2:
                dp[i][j] = max(A[i], A[j])
            else:
                left = A[i] + min(dp[i+2][j], dp[i+1][j-1])
                right = A[j] + min(dp[i+1][j-1], dp[i][j-2])
                dp[i][j] = max(left, right)

    total_sum = sum(A)
    alice_sum = dp[0][n-1]
    bob_sum = total_sum - alice_sum

    if alice_sum > bob_sum:
        return 'Alice'
    elif alice_sum < bob_sum:
        return 'Bob'
    else:
        return 'Tie'
