def winner(A):
    dp = [[0] * len(A) for _ in range(len(A))]
    for i in range(len(A)):
        dp[i][i] = A[i]
    for i in range(len(A)-1, -1, -1):
        for j in range(i+1, len(A)):
            dp[i][j] = max(A[i] - dp[i+1][j], A[j] - dp[i][j-1])
    return 'Alice' if dp[0][len(A)-1] > 0 else 'Bob'
