def is_beautiful(arr):
    for i in range(len(arr) - 1):
        if arr[i] % arr[i+1] == 0 or arr[i+1] % arr[i] == 0:
            return False
    return True

def find_smallest_d(arr):
    for d in range(1, max(arr)+1):
        temp_arr = arr.copy()
        for i in range(len(temp_arr)):
            if temp_arr[i] % d == 0:
                temp_arr[i] = 'red'
            else:
                temp_arr[i] = 'blue'
        if is_beautiful(temp_arr):
            return d
    return 0
