def find_smallest_d(array):
    for d in range(1, max(array) + 1):
        colors = [0] * len(array)
        for i in range(len(array)):
            if array[i] % d == 0:
                colors[i] = 1
        if all(colors[i] != colors[i + 1] for i in range(len(array) - 1)):
            return d
    return 0
