def find_smallest_d(arr):
    for d in range(1, max(arr)+1):
        colored_arr = []
        for num in arr:
            if num % d == 0:
                colored_arr.append("red")
            else:
                colored_arr.append("blue")
        is_beautiful = True
        for i in range(1, len(colored_arr)):
            if colored_arr[i] == colored_arr[i-1]:
                is_beautiful = False
                break
        if is_beautiful:
            return d
    return 0
