def beautiful_coloring(arr):
    max_val = max(arr)
    for d in range(1, max_val + 1):
        coloring = [0] * len(arr)
        for i in range(len(arr)):
            if arr[i] % d == 0:
                coloring[i] = 1
        if all(coloring[i] != coloring[i+1] for i in range(len(coloring)-1)):
            return d
    return 0
