def count_numbers(n):
    count = 0
    sqrt_max = int(n ** 0.5)
    cube_max = int(n ** (1/3))

    for i in range(1, sqrt_max + 1):
        if i ** 2 <= n:
            count += 1

    for i in range(1, cube_max + 1):
        if i ** 3 <= n and i ** 2 != i ** 3:
            count += 1

    return count
