def generate_BA(k, s, x):

    def backtrack(index, current_string):
        nonlocal count, result

        if index == len(s):
            count += 1
            if count == x:
                result = current_string
            return

        if s[index] == '*':
            for i in range(k+1):
                backtrack(index+1, current_string + 'b'*i)
        else:
            backtrack(index+1, current_string + s[index])

    count = 0
    result = ''
    backtrack(0, '')
    return result
