def intersection(interval1, interval2):
    # Find the maximum starting point and the minimum ending point of the two intervals
    starting_point = max(interval1[0], interval2[0])
    ending_point = min(interval1[1], interval2[1])

    # Check if there is an intersection
    if starting_point <= ending_point:
        # Check if the intersection is a single point or has a length larger than 1
        if starting_point == ending_point:
            return starting_point
        else:
            return (starting_point, ending_point)
    else:
        return None
