def calculate_expression(integers, operations):
    result = integers[0]
    for i in range(1, len(integers)):
        if operations[i-1] == '+':
            result += integers[i]
        elif operations[i-1] == '-':
            result -= integers[i]
        elif operations[i-1] == '*':
            result *= integers[i]
        elif operations[i-1] == '/':
            result /= integers[i]
    return result
