def sort_books(n, m, bookshelf):
    def compare_strings(a, b, odd):
        a_chars = list(a)
        b_chars = list(b)

        for i in range(m):
            if i % 2 == odd:
                if a_chars[i] < b_chars[i]:
                    return -1
                elif a_chars[i] > b_chars[i]:
                    return 1

        return 0

    indices = list(range(1, n+1))

    sorted_indices = sorted(indices, key=lambda x: bookshelf[x-1])
    sorted_indices = sorted(sorted_indices, key=lambda x: compare_strings(bookshelf[x-1], bookshelf[sorted_indices[0]-1], 0))
    sorted_indices = sorted(sorted_indices, key=lambda x: compare_strings(bookshelf[x-1], bookshelf[sorted_indices[0]-1], 1))

    return sorted_indices
