import datetime

def parse_iso8601(date_str, delimiter='T', timezone=None):
    try:
        datetime_obj = datetime.datetime.strptime(date_str, f"%Y-%m-%d{delimiter}%H:%M:%S%z")
        unix_timestamp = datetime_obj.timestamp()
        return str(int(unix_timestamp))
    except ValueError:
        return None
