import itertools

def count_permutations(n, m, k, p):
    count = 0

    for permutation in itertools.permutations(range(1, n+1)):
        max_values = []
        god_numbers = []

        for i in range(len(permutation)):
            subsegment = permutation[:i+1]

            if len(set(subsegment)) == m:
                max_value = max(subsegment)
                max_values.append(max_value)

        god_numbers = [number for number in set(max_values) if max_values.count(number) == m]

        if len(god_numbers) == k:
            count += 1

    return count % p
