def find_minimal_difference(a, b, c):
    total_duration = a + 2 * b + 3 * c
    half_duration = total_duration // 2

    dp = [0] * (half_duration + 1)
    for i in range(a, half_duration + 1):
        dp[i] = max(dp[i], dp[i - a] + a)
    for i in range(b, half_duration + 1):
        dp[i] = max(dp[i], dp[i - b] + b)
    for i in range(c, half_duration + 1):
        dp[i] = max(dp[i], dp[i - c] + c)

    return total_duration - 2 * dp[half_duration]
