def minimal_possible_difference(a, b, c):
    songs = [1] * a + [2] * b + [3] * c
    total_duration = sum(songs)
    half_duration = total_duration // 2
    current_duration = 0
    for i in range(len(songs)):
        current_duration += songs[i]
        if current_duration >= half_duration:
            return abs((total_duration - current_duration) - current_duration)
