def find_min_difference(a, b, c):
    total_duration = a + b + c
    half_duration = total_duration // 2

    # Generate all possible sums using a, b, and c
    possible_sums = set()
    for i in range(a + 1):
        for j in range(b + 1):
            for k in range(c + 1):
                possible_sums.add(i + j*2 + k*3)

    # Find the minimum difference between two concert durations
    min_difference = float("inf")
    for duration in possible_sums:
        if abs(duration - half_duration) < min_difference:
            min_difference = abs(duration - half_duration)

    return min_difference
