def min_concert_duration_diff(a, b, c):
    total_minutes = a + 2*b + 3*c
    target_minutes = total_minutes // 2
    dp = [0] * (target_minutes + 1)
    for i in range(1, a+1):
        for j in range(target_minutes, i-1, -1):
            dp[j] = max(dp[j], dp[j-i] + i)
    for i in range(1, b+1):
        for j in range(target_minutes, i*2-1, -1):
            dp[j] = max(dp[j], dp[j-i*2] + i*2)
    for i in range(1, c+1):
        for j in range(target_minutes, i*3-1, -1):
            dp[j] = max(dp[j], dp[j-i*3] + i*3)
    return total_minutes - 2*dp[target_minutes]

# Example usage
print(min_concert_duration_diff(1, 2, 3))
