def find_x_values(a):
    n = len(a)
    dp = [[0 for _ in range(1024)] for _ in range(n)]
    result = []

    for i in range(n):
        dp[i][a[i]] = 1

        for j in range(i):
            if a[j] < a[i]:
                for k in range(1024):
                    if dp[j][k] == 1:
                        dp[i][k ^ a[i]] = 1

    for i in range(1024):
        if dp[n - 1][i] == 1:
            result.append(i)

    return result
