def find_x(a):
    n = len(a)
    dp = [[0] * 2048 for _ in range(n)]
    result = []

    for i in range(n):
        dp[i][a[i]] = 1
        result.append(a[i])

        for j in range(i):
            if a[i] > a[j]:
                for k in range(2048):
                    if dp[j][k]:
                        dp[i][k ^ a[i]] = 1
                        result.append(k ^ a[i])

    return sorted(result)
