def game_winner(A):
    n = len(A)
    dp = [[0] * n for _ in range(n)]

    for i in range(1, n):
        dp[i][i-1] = 0

    for length in range(2, n+1):
        for i in range(n - length + 1):
            j = i + length - 1
            dp[i][j] = max(A[i] - dp[i+1][j], A[j] - dp[i][j-1])

    if dp[0][n-1] >= 0:
        return 'Alice'
    else:
        return 'Bob'
