def winner_of_game(A):
    N = len(A)
    dp = [[0] * N for _ in range(N)]

    for i in range(N):
        dp[i][i] = 1

    for length in range(2, N + 1):
        for i in range(N - length + 1):
            j = i + length - 1
            if length == 2 and A[i] < A[j]:
                dp[i][j] = 2
            elif A[i] < A[j]:
                dp[i][j] = max(A[i] if A[i + 1] > A[j] else A[j], dp[i + 1][j - 1])
            else:
                dp[i][j] = max(dp[i][j - 1], dp[i + 1][j])

    if dp[0][N - 1] % 2 == 0:
        return 'Bob'
    else:
        return 'Alice'
