def whoWinsGame(A):
    n = len(A)
    dp = [[0] * n for _ in range(n)]
    
    for i in range(n):
        dp[i][i] = 1
    
    for length in range(2, n + 1):
        for i in range(n - length + 1):
            j = i + length - 1
            if A[i] < A[i+1]:
                dp[i][j] = 1
            else:
                for k in range(i + 1, j + 1):
                    if dp[i][k-1] == 1 and dp[k][j] == 1:
                        dp[i][j] = 1
                        break
    
    if dp[0][n-1] == 1:
        return 'Alice'
    else:
        return 'Bob'
