def winner_of_game(A):
    n = len(A)
    alice_turn = True
    while n > 0:
        if A[0] < A[-1]:
            if alice_turn:
                A.pop(0)
            else:
                A.pop(-1)
        else:
            if alice_turn:
                A.pop(-1)
            else:
                A.pop(0)
        n -= 1
        alice_turn = not alice_turn

    if alice_turn:
        return 'Bob'
    else:
        return 'Alice'
