def winner_of_game(A):
    n = len(A)
    sum_A = sum(A)

    # Initialize the scores for Alice and Bob
    alice_score = 0
    bob_score = 0

    while n > 0:
        if n % 2 == 0:
            # Alice's turn
            if A[0] > A[n-1]:
                alice_score += A[0]
                A = A[1:]
            else:
                alice_score += A[n-1]
                A = A[:-1]
        else:
            # Bob's turn
            if A[0] > A[n-1]:
                bob_score += A[0]
                A = A[1:]
            else:
                bob_score += A[n-1]
                A = A[:-1]

        n -= 1

    if alice_score > bob_score:
        return 'Alice'
    else:
        return 'Bob'
