def smallest_beautiful_d(array):
    def check_beautiful(d):
        for i in range(1, len(array)):
            if array[i] % d == 0 and array[i-1] % d == 0:
                return False
        return True

    for d in range(1, max(array) + 1):
        if check_beautiful(d):
            return d
    return 0
