def find_smallest_d(arr):
    for d in range(1, max(arr) + 1):
        colors = []
        for element in arr:
            if element % d == 0:
                colors.append('red')
            else:
                colors.append('blue')

            if len(colors) > 1 and colors[-1] == colors[-2]:
                break
        else:
            return d

    return 0
