def find_smallest_d(array):
    if len(array) < 2:
        return 0

    distinct_elements = set(array)

    for d in distinct_elements:
        colored = [0] * len(array)
        prev_color = 1
        f_prev = array[0] % d != 0

        for i in range(len(array)):
            if array[i] % d == 0:
                colored[i] = 1

            if colored[i] == colored[i-1] and colored[i] != 0:
                break

        else:
            return d

    return 0
