def beautiful_coloring(arr):
    def is_beautiful(d):
        for i in range(1, len(arr)):
            if (arr[i] % d == 0 and arr[i-1] % d == 0) or (arr[i] % d != 0 and arr[i-1] % d != 0):
                return False
        return True

    for d in range(1, max(arr)+1):
        if is_beautiful(d):
            return d
    return 0
