def beautiful_coloring(arr):
    for d in range(1, max(arr)+1):
        coloring = []
        for element in arr:
            if element % d == 0:
                coloring.append("red")
            else:
                coloring.append("blue")
        if all(coloring[i] != coloring[i+1] for i in range(len(coloring)-1)):
            return d
    return 0
