def find_lexicographically_smallest(k, x, s):
    count = 0
    result = ""
    for i in range(len(s)):
        if s[i] == '*':
            for j in range(k + 1):
                count += 1
                if count == x:
                    result += 'b' * j + 'a' + 'b' * (k - j)
                    break
        else:
            result += s[i]
    return result
