def find_xth_BA_string(k, x, s):
    def generate_BA_strings(curr_string, remaining_stars):
        if len(curr_string) == len(s):
            all_BA_strings.append(curr_string)
            return
        if s[len(curr_string)] == 'a':
            generate_BA_strings(curr_string + 'a', remaining_stars)
        else:
            for i in range(remaining_stars + 1):
                generate_BA_strings(curr_string + 'b'*i, remaining_stars - i)
    all_BA_strings = []
    generate_BA_strings('', k)
    all_BA_strings.sort()
    return all_BA_strings[x-1]
