def perform_binary_xor(str1, str2):
    xor_result = ""
    for i in range(len(str1)):
        if str1[i] == "0" and str2[i] == "0":
            xor_result += "0"
        elif str1[i] == "1" and str2[i] == "0":
            xor_result += "1"
        elif str1[i] == "0" and str2[i] == "1":
            xor_result += "1"
        elif str1[i] == "1" and str2[i] == "1":
            xor_result += "0"
    return xor_result
