def is_valid_date(date_string):
    try:
        month, day, year = date_string.split('-')
        month = int(month)
        day = int(day)
        year = int(year)

        if month < 1 or month > 12:
            return False

        if day < 1 or day > 31:
            return False

        if month in [4, 6, 9, 11] and day > 30:
            return False

        if month == 2:
            if (year % 4 == 0 and year % 100 != 0 or year % 400 == 0) and day > 29:
                return False
            elif day > 28:
                return False

        return True
    except:
        return False
