def is_valid_date(date_string):
    try:
        month, day, year = date_string.split('-')
        month = int(month)
        day = int(day)
        year = int(year)
        if 1 <= month <= 12:
            if month == 2:
                if year % 4 == 0 and (year % 100 != 0 or year % 400 == 0):
                    return 1 <= day <= 29
                else:
                    return 1 <= day <= 28
            elif month in [4, 6, 9, 11]:
                return 1 <= day <= 30
            else:
                return 1 <= day <= 31
    except ValueError:
        return False
    return False
