def is_valid_date(date):
    try:
        # Check if input string has correct format 'mm-dd-yyyy'
        date_parts = date.split('-')
        if len(date_parts) != 3:
            return False

        month = int(date_parts[0])
        day = int(date_parts[1])
        year = int(date_parts[2])

        # Check if month is valid
        if month < 1 or month > 12:
            return False

        # Check if day is valid
        if day < 1 or day > 31:
            return False

        # Check if the number of days is correct for the corresponding month
        if month == 2:
            if year % 4 == 0 and (year % 100 != 0 or year % 400 == 0):
                max_days = 29
            else:
                max_days = 28
        elif month in [4, 6, 9, 11]:
            max_days = 30
        else:
            max_days = 31

        if day > max_days:
            return False

        return True
    except:
        return False
