def is_valid_date(date_string):
    try:
        month, day, year = map(int, date_string.split('-'))
        if month < 1 or month > 12 or day < 1 or day > 31:
            return False
        if month == 2:
            if year % 4 == 0 and (year % 100 != 0 or year % 400 == 0):
                if day > 29:
                    return False
            elif day > 28:
                return False
        elif month in [4, 6, 9, 11]:
            if day > 30:
                return False
        return True
    except ValueError:
        return False
