def find_intersection(interval1, interval2):
    # Check if intervals are valid
    if interval1[1] < interval1[0] or interval2[1] < interval2[0]:
        return None

    # Find intersection range
    start = max(interval1[0], interval2[0])
    end = min(interval1[1], interval2[1])

    # Check length of intersection
    if end - start > 1:
        return (start, end)
    else:
        return end
